import React from 'react'
import {Link} from 'react-router-dom'
import {useState} from 'react'
import Button from './Button'
import DonatorLogin from './donatorLogin'
import DonatorSignup from './donatorSignup'
import { useHistory } from "react-router-dom"
const Donator = () => {

    const history = useHistory()
    const [unclick, setUnclick] = useState(true)
    const [showlogin, setLogin] = useState(true)
    const getInput = (e) => {

        console.log(e)

        setUnclick(!unclick)
    }

    const clickLogin = (e) => {
        setUnclick(!unclick)
        // console.log(e)
        if (e.target.innerText !== "Login"){
            setLogin(!showlogin)
            history.push("/donatorsignup")
        }
        else{
            history.push("/donatorlogin")
        }

    }

    return (

          <div>
            <div><h1 style={{color:"#92eb49", textAlign:"center", minHeight:"20vh", marginTop:"10vh"}}>User reigistration and login</h1></div>
            {unclick && <div className="seperatebutton">
                <div><Button buttonname="Login" onClick={clickLogin}/></div>
                <div><Button buttonname="Signup" onClick={clickLogin}/></div>
            </div>}
            { !unclick ? showlogin ? 
            (<DonatorLogin onSubmit={getInput}/>) : 
            (<DonatorSignup onSubmit={getInput}/>) : ""
            }
          </div>

    )
}

export default Donator
