import React from 'react'
import {Link} from 'react-router-dom'
import {useState} from 'react'
import Button from './Button'
import DonatorLogin from './donatorLogin'
import DonatorSignup from './donatorSignup'

const Donator = () => {

    const [unclick, setUnclick] = useState(true)
    const [showlogin, setLogin] = useState(true)
    const submitInput = (e) => {

        console.log(e)

    }

    const clickLogin = (e) => {
        setUnclick(!unclick)
        // console.log(e)
        if (e.target.innerText !== "Login"){
            setLogin(!showlogin)
        }

    }

    return (

          <div>
            <div><h1 style={{color:"#92eb49", textAlign:"center", minHeight:"20vh"}}>User reigistration and login</h1></div>
            {unclick && <div className="seperatebutton">
                <div><Button buttonname="Login" onClick={clickLogin}/></div>
                <div><Button buttonname="Signup" onClick={clickLogin}/></div>
            </div>}
            { !unclick ? showlogin ? 
            (<DonatorLogin onSubmit={() => submitInput()}/>) : 
            (<DonatorSignup onSubmit={() => submitInput()}/>) : ""
            }
          </div>

    )
}

export default Donator
