import React from 'react'
import {Link} from 'react-router-dom'
import {useState} from 'react'
import Button from './Button'

const Donator = () => {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [unclick, setUnclick] = useState(true)
    const [showlogin, setLogin] = useState(true)
    const submitInput = (e) => {
        console.log(username)
        console.log(password)
        setUsername('')
        setPassword('')

    }

    const clickLogin = (e) => {
        setUnclick(!unclick)
        console.log(e)
        if (e.target.innerText !== "Login"){
            setLogin(!showlogin)
        }

    }

    return (

          <div>
            <div><h1 style={{color:"#92eb49", textAlign:"center", minHeight:"20vh"}}>User reigistration and login</h1></div>
            {unclick && <div className="seperatebutton">
                <div><Button buttonname="Login" onClick={clickLogin}/></div>
                <div><Button buttonname="Signup" onClick={clickLogin}/></div>
            </div>}
            { !unclick && showlogin ? 
            (<div >

                    <form action="" className="labelinput" onSubmit={submitInput}>
                    <h3>Login</h3>
                    {/* <label htmlFor="" className="label">
                        Username:
                    </label> */}
                        <input type="text" className="label" value={username}  placeholder="Username" onChange={(event) => setUsername(event.target.value)}/>
                        {/* <label htmlFor="" className="label">Password:</label> */}   
                        <input type="text" className="label" value={password} placeholder="Password" onChange={(event) => setPassword(event.target.value)}/>
                        <input type="submit"  value="Enter" className="submit" />
                    </form>
            </div>) : 
            (<div >

                    {/* <label htmlFor="" className="label">
                        Username:
                    </label> */}
                    <form action="" className="labelinput" onSubmit={submitInput}>
                    <h3>Signup</h3>

                    <input type="text" className="label" value={username}  placeholder="Username" onChange={(event) => setUsername(event.target.value)}/>



                    {/* <label htmlFor="" className="label">Password:</label> */}


                    <input type="text" className="label" value={password} placeholder="Password" onChange={(event) => setPassword(event.target.value)}/>



                    <input type="submit"  value="Enter" className="submit" />

                    </form>
            </div>)
            }
          </div>

    )
}

export default Donator
