import Web3 from 'web3'

const Donatorinterface = () => {
    let admins = require('./ABI.json');
    const abi = admins[0].abi
    const currentAccount = useLocation().pathname.split('/donator/')[1];
    var web3 = new Web3();
    var balance1;
    web3 = new Web3('HTTP://127.0.0.1:8545');

    const getBalance = (e) => {

        web3.eth.getBalance(currentAccount, function (error, wei) {
            if (!error) {
                var balance = web3.utils.fromWei(wei, 'ether');
                // alert(balance + " ETH");

                return <p>{balance}</p>
            }
        });
    }





    return (
        <div>
            {currentAccount}
            {/* <button className="smallbutton" onClick={getBalance}>getBalance</button> */}
            <p>balance: </p><span><p id="balance"></p></span>
        </div>
    )
}

export default Donatorinterface
