import Web3 from 'web3'
import React from 'react'

var web3 = new Web3();

const init = () => {
  if (typeof web3 !== 'undefined') {
    console.log('Web3 found');
    window.web3 = new Web3(web3.currentProvider);
    web3.eth.defaultAccount = web3.eth.accounts[0];
  } else {
     console.error('web3 was undefined');
  }
}

const Test = () => {

    const abi = [
        [
            {
                "inputs": [
                    {
                        "internalType": "address",
                        "name": "newOwner",
                        "type": "address"
                    }
                ],
                "name": "changeOwner",
                "outputs": [],
                "stateMutability": "nonpayable",
                "type": "function"
            },
            {
                "inputs": [],
                "stateMutability": "nonpayable",
                "type": "constructor"
            },
            {
                "anonymous": false,
                "inputs": [
                    {
                        "indexed": true,
                        "internalType": "address",
                        "name": "oldOwner",
                        "type": "address"
                    },
                    {
                        "indexed": true,
                        "internalType": "address",
                        "name": "newOwner",
                        "type": "address"
                    }
                ],
                "name": "OwnerSet",
                "type": "event"
            },
            {
                "inputs": [],
                "name": "getOwner",
                "outputs": [
                    {
                        "internalType": "address",
                        "name": "",
                        "type": "address"
                    }
                ],
                "stateMutability": "view",
                "type": "function"
            }
        ]
    ]
    // contractAddress
    const getBlock = () => {
        var web3;
        web3 = new Web3(web3.currentProvider);
        // return web3.eth.getBlock('latest', (err, result) => {console.log(1)})
        const contractAddress = '0x5A8391EAc93D5a9e4975fE3baAD5306e6DABd2E4'
        var contract = new web3.eth.Contract(abi, contractAddress)
        console.log(contract)
    }
    return (
        <div>
            <button onClick={() => getBlock()}>??</button>
        </div>
    )
}

export default Test
