    var web3 = new Web3();
const init = () => {
  if (typeof web3 !== 'undefined') {
    console.log('Web3 found');
    window.web3 = new Web3(web3.currentProvider);
    web3.eth.defaultAccount = web3.eth.accounts[0];
  } else {
     console.error('web3 was undefined');
  }
}
// get abi
const abi = [
    [
        {
            "inputs": [
                {
                    "internalType": "address",
                    "name": "newOwner",
                    "type": "address"
                }
            ],
            "name": "changeOwner",
            "outputs": [],
            "stateMutability": "nonpayable",
            "type": "function"
        },
        {
            "inputs": [],
            "stateMutability": "nonpayable",
            "type": "constructor"
        },
        {
            "anonymous": false,
            "inputs": [
                {
                    "indexed": true,
                    "internalType": "address",
                    "name": "oldOwner",
                    "type": "address"
                },
                {
                    "indexed": true,
                    "internalType": "address",
                    "name": "newOwner",
                    "type": "address"
                }
            ],
            "name": "OwnerSet",
            "type": "event"
        },
        {
            "inputs": [],
            "name": "getOwner",
            "outputs": [
                {
                    "internalType": "address",
                    "name": "",
                    "type": "address"
                }
            ],
            "stateMutability": "view",
            "type": "function"
        }
    ]
]

    // contractAddress
    const getOwner = () => {
        // init();
        web3 = new Web3('http://localhost:8545')
        // return web3.eth.getBlock('latest', (err, result) => {console.log(1)})
        const contractAddress = '0x5A8391EAc93D5a9e4975fE3baAD5306e6DABd2E4'
        const contract = new web3.eth.Contract(abi, contractAddress)
        console.log(contract)
        const res = contract.methods.getOwner().call((err, result) => {
            if (err){
                console.log(err)
            } else {
                console.log(result)
            }
        })
        console.log(res)
    }
    return (
        <div>
            <button onClick={() => getOwner()}>??</button>
        </div>
    )
}

export default Test
